INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q535586"), "NULL", "NULL", "NULL", "في الأصل كان المتحف فيلا للمهندس وليام ولكوكس حيث كان يعمل كبير مهندسي خزان اسوان ، لذلك وجب إقامته بجوار عمله ، واثناء الحفر لبناء السد العالي تم العثور على العديد من الآثار و الحفريات بالإضافة للآثار التي اكتشفت في جزيرة الفنتين الزاخرة بالحفريات الأثرية التي لم تكتشف بعد لذلك تم تحويل هذه الفيلا لمتحف يضم أهم آثار جزيرة الفنتين و بعض الآثار النوبية كتماثيل لأمنحتب الثالث و بعض الآلهة المصرية االقديمة و ابرزهم خنوم على هيئة كبش و بعض مومياوات لكباش رمزاً لخنوم ،بعض اللوحات الجنائزية ، و احواض من حجر الشست ترجع لعصور ماقبل التاريخ و بعض الأسلحة و الأواني الفخارية ، يتكون المتحف من ساحة عرض كبيرة و مخزن تحت الأرض لتخزين الآثار التي لا تعرض او تحتاج لترميم و صيانة ، و ساحة العرض تتكون من 4 قاعات قاعتين علي جهة اليمين و قاعتين على جهة اليسار .  و تم افتتاح ملحق للمتحف عام 1993م يتكون من 3 قاعات عرص تحوي 670 قطعة اثرية متنوعة بدايةً من عصر الدولة القديمة مروراً بالحقبة القبطية حتى العصر الروماني .", "https://yallabook.com/guide/show.php?nid=1016&%D9%85%D8%AA%D8%AD%D9%81-%D8%A7%D8%B3%D9%88%D8%A7%D9%86", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q948882"), "NULL", "NULL", "مكتبة البرازيل الوطنية", "مكتبة البرازيل الوطنية تم تأسيس مكتبة البرازيل الوطنية في مدينة 'ريو دى جانيرو' عام 1837 على يد مجموعة من 43 مهاجرا برتغاليا أرادوا تعزيز الثقافة في أنحاء المجتمع البرتغالي الذي يعيش في البرازيل، لكن البناء لم يبدأ فعليا حتى عام 1880 من قبل المهندس المعماري البرتغالي 'رفائيل دا سيلفا'، و هي مبنية على طراز 'نيو مانويل'، مع تصميم خارجي من الحجر الجيري مزين بتماثيل 4 مستكشفين برتغاليين.", "https://www.jamhara.com/post/%D9%85%D9%83%D8%AA%D8%A8%D8%A9-%D8%A7%D9%84%D8%A8%D8%B1%D8%A7%D8%B2%D9%8A%D9%84-%D8%A7%D9%84%D9%88%D8%B7%D9%86%D9%8A%D8%A9", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q948882"), "NULL", "البيان", "مكتبة البرازيل الوطنية مستودعُ المعرفة", "داخل ميدان «سينلانديا» الموجود في قلب مدينة ريو دي جانيرو البرازيلية، تلك المنطقة التي تضم عدداً من المعالم، من بينها مسرح البلدية والمتحف الوطني للفنون الجميلة، يشع نور حضاري من مبنى المكتبة الوطنية البرازيلية التي تعد أكبر مكتبة في أميركا اللاتينية والسابعة في العالم.. وتستحق بكل جدارة أن تكون مستودع التراث الوثائقي والبيبلوغرافي للبرازيل، بسبب غناها بالمجموعات القيمة التي تصل إلى نحو تسعة ملايين مادة، وبسبب كونها أيضاً أول من نظم صفوفاً في علــم المكتبة في أميركا اللاتينية. تاريخ المكتبة بدأ تاريخ المكتبة الوطنية عام 1755، وذلك عندما تعرضت مدينة ليسبون البرتغالية لزلزال عنيف، حيث كانت المكتبة الملكية تعتبر من أهم المكتبات في أوروبا في ذلك الوقت، وكانت الخسارة غير القابلة للتعويض للبرتغاليين، الدافع إلى نقل العديد من محتوياتها إلى البرازيل. تم جلب المجموعة على ثلاث مراحل: الأولى كانت في عام 1810، إضافة إلى اثنتين في عام 1811، وفي بداية الأمر تمت استضافة المكتبة، ضمن الغرف العلوية لمشفى «ثيرد أوردر أوف كارمل»، الموجودة إلى جانب القصر الإمبراطوري، وذلك طبقاً لميثاق أبرم عام 1810.", "https://www.albayan.ae/books/library-visit/2014-11-07-1.2238264", "NULL", "2014-11-07 02:03+0400", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q948882"), "NULL", "Atlas Obscura", "This Stunning Library Is the Largest in Latin America", "The Biblioteca Nacional do Brasil in Rio de Janeiro is the largest library in Latin America and the seventh largest in the world. But were it not for a devastating earthquake and a Napoleonic invasion, it may never have reached such heights.", "https://www.atlasobscura.com/places/biblioteca-nacional-do-brasil", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q7201222"), "NULL", "NULL", "PSC", "يهدف مركز القبة السماوية العلمي إلى تحفيز وتنمية وعي واهتمام واستيعاب الجمهور للعلوم من خلال مجموعة من البرامج والمعارض التعليمية الشيقة المنتقاة، كما يطمح أن يكون مركز إقليمي ونموذج وطني لتوصيل العلوم. ويقوم المركز بتبسيط وتوصيل مبادئ العلوم لشريحة كبيرة من الجمهور، مسلطًّا الضوء على طلاب المدارس والجامعات.", "https://www.bibalex.org/psc/ar/home/index.aspx", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q7201222"), "NULL", "Bibalex.org", "مركز القبة السماوية العلمي", "وقد قام بتأسيس واستضافة مكتب سكرتارية رابطة المراكز العلمية بشمال إفريقيا والشرق الأوسط NAMES ٢٠٠٦.  ويعمل المركز على تحقيق أهدافه من خلال تشجيع الناس على حب العلم والتخيل والابتكار من خلال مجموعة متنوعة من الأنشطة تُقدم ضمن ثلاثة أقسام، كل منها يتناول العلم بطريقة مختلفة وغير تقليدية نسبيًّا بالنسبة للمجتمع المصري: القبة السماوية: كعضو في جمعية القباب الفلكية العالمية (IPS)، تعتبر القبة السماوية مرفقًا متقدمًا يستخدم أحدث ما توصلت إليه التكنولوجيا في مجال العرض.", "https://www.bibalex.org/ar/center/details/planetariumsciencecenterpsc", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q58973805"), "NULL", "دنيا الوطن", "الجمعية المغربية للحريات الدينية تنظم الدورة الثالثة لمؤتمر الأقليات الدينية | دنيا الوطن", "رام الله - دنيا الوطن تحت شعار 'نحو صياغة مشروع وطني لحماية حقوق الأقليات الدينية ومواجهة ظاهرة العداء للجاليات المسلمة'، تنظم الجمعية المغربية للحريات الدينية، بشراكة مع جمعية 'أمور' للثقافة الأمازيغية، وجمعية انطلاقة، وبتنسيق مع الكنيسة الكاثوليكية والكنيسة الإنجيلية بالرباط، الدورة الثالثة للمؤتمر الوطني للأقليات الدينية طيلة يوم 29 نوفمبر 2019 بمقر مؤسسة 'الموافقـة' المسيحية للربـاط ابتداء من الساعة التاسعة صباحا.", "https://www.alwatanvoice.com/arabic/news/2019/10/31/1287660.html", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
