INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q8247531"), "NULL", "القرن الأفريقي بوست", "أرقى المكتبات الأفريقية.. المكتبة الوطنية الصومالية عنوان التاريخ والثقافة - القرن الأفريقي بوست", "أرقى المكتبات الأفريقية.. المكتبة الوطنية الصومالية عنوان التاريخ والثقافة ولدت فكرة إنشاء المكتبة الوطنية الصومالية في عام 1975م، وافتتحت بشكل رسمي في عام 1986م، وأصبحت مهوى قلوب الصوماليين، إذ كانوا يأتون من كل بقعة من الوطن، إلى مبنى المكتبة للاستفادة منها.", "https://hafpost.net/articles-and-analyzes/7797/", "NULL", "2019-09-08T11:22:46+03:00", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q8247531"), "NULL", "NULL", "المكتبة الوطنية الصومالية – منبع المعرفة وتراث حضاري", "إذا كان حديثنا له صلة بالمكتبات ولاسيما فيما يتعلق بالمكتبة الوطنية في بلاد الصومال على هامش افتتاحها واستئناف عملها من جديد لتأخذ دورها الريادي لا مانع من أن نقدِّم هنا عرضا بسيطا في أنواع المكتبات وسماتها المتميزة، ومن هنا وباقتصار شديد فإن المكتبات في العالم عبر العصور التاريخية المختلفة تنقسم إلى قسمين: المكتبة العامة والمكتبة الخاصة.", "http://arab-afli.org/main/post_details.php?alias=%D8%A7%D9%84%D9%85%D9%83%D8%AA%D8%A8%D8%A9_%D8%A7%D9%84%D9%88%D8%B7%D9%86%D9%8A%D8%A9_%D8%A7%D9%84%D8%B5%D9%88%D9%85%D8%A7%D9%84%D9%8A%D8%A9_%E2%80%93_%D9%85%D9%86%D8%A8%D8%B9_%D8%A7%D9%84%D9%85%D8%B9%D8%B1%D9%81%D8%A9_%D9%88%D8%AA%D8%B1%D8%A7%D8%AB_%D8%AD%D8%B6%D8%A7%D8%B1%D9%8A", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q57395129"), "NULL", "NULL", "NULL", "يقع متحف الوادى الجديد في مدينة الخارجة عاصمة محافظة الوادى الجديد، ويضم  مجموعات متنوعة من الآثار التي عثر عليها في المناطق الأثرية بالوادي الجديد، والتي تحكي تاريخ الحضارة المصرية بداية من عصور ما قبل التاريخ حتى العصر الحديث. ومن المقتنيات القيمة بالمتحف مجموعة من القطع الأثرية التي ترجع إلى عصور ما قبل التاريخ، وتشمل عدداً من الأواني الحجرية، والسكاكين، والمكاشط الظرانية. كما يشمل العرض المتحفي مجموعة نادرة من التماثيل واللوحات الجنائزية التي تخص حكام الواحات، ومجموعة أخرى من تماثيل الآلهة.", "http://www.antiquities.gov.eg/DefaultAr/Museum/Pages/MuseumDetails.aspx?MusCode=29", "NULL", "NULL", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q57395129"), "NULL", "الوطن", "متحف الوادي الجديد يحتفل باليوم العالمي للمتاحف", "يقع متحف آثار الوادي الجديد في مدينة الخارجة ويضم آثارا مصرية من عصور ما قبل التاريخ وحتى العصر الحديث والتي عثر عليها في في بمختلف المراكز الإدارية والمناطق الأثرية بربوع المحافظة.", "https://www.elwatannews.com/news/details/4166039", "NULL", "02:44 م | الأحد 19 مايو 2019", "2020-01-10 00:00:00", "example-1");
INSERT INTO reference(article_id, section_id, publisher_name, publication_title, summary, url, quality, publication_date, retrieved_date, content_selection_method) VALUES ((SELECT id FROM article WHERE wd_q_id = "Q57395129"), "NULL", "اليوم السابع", "تنظيم معرض عالمى بمتحف آثار الوادى الجديد نوفمبر القادم  - اليوم السابع", "وأشار إلى أن،اختيار متحف آثار الوادي الجديد لاستضافه واحتضان هذا المعرض العالمي يعد بمثابة نقلة كبيرة،وأن المتحف يمكن أن يدخل التصنيف العالمي للمتاحف من خلال استضافة المعرض، كما أن مدير عام الآثار المهددة بالانقراض،الدكتور روبرت بيولى،وجامعه إكس فورد،اختاروا متحف الوادي الجديد، لمقاييس عالميه خاصة بالمعرض وأيضا لنشاط المعرض المستمر طوال العام.", "https://www.youm7.com/story/2019/10/9/%D8%AA%D9%86%D8%B8%D9%8A%D9%85-%D9%85%D8%B9%D8%B1%D8%B6-%D8%B9%D8%A7%D9%84%D9%85%D9%89-%D8%A8%D9%85%D8%AA%D8%AD%D9%81-%D8%A2%D8%AB%D8%A7%D8%B1-%D8%A7%D9%84%D9%88%D8%A7%D8%AF%D9%89-%D8%A7%D9%84%D8%AC%D8%AF%D9%8A%D8%AF-%D9%86%D9%88%D9%81%D9%85%D8%A8%D8%B1-%D8%A7%D9%84%D9%82%D8%A7%D8%AF%D9%85/4451082", "NULL", "2019-10-09T12:56:45Z", "2020-01-10 00:00:00", "example-1");
